import {Firestore} from '../../config/firebase'
import {types} from './actionTypes';
import {SuccessMsg, ErrorMsg} from '../../utils/helpers/msg'

// Add Todo
export const addTodo = newToDo => async dispatch => {

    dispatch({
        type: types.ADD_TODO,
        addingNewTodo: true
    })

    Firestore.collection('todos').add(newToDo).then(docRef => {
        let data = null
        data = {
            ...newToDo,
            id: docRef.id
        }
        dispatch({
            type: types.ADD_TODO, 
            todo: data,
            addingNewTodo: false
        });

        SuccessMsg('Added successfully')

    }).catch(err => {
        ErrorMsg(err.message)
    })

};


// Remove Todo
export const removeTodo = id => async dispatch => {
    Firestore.collection('todos').doc(id).delete().then(() => {
        SuccessMsg('Deleted successfully')
        dispatch({type: types.DELETE_TODO, id: id});
    }).catch((err) => {
        ErrorMsg(err.message)
    });


};


// Move Todo
export const moveTodo = (id, bool) => async dispatch => {
    Firestore.collection('todos').doc(id).update({completed: bool}).then(() => {
        if (bool) {
            SuccessMsg('Moved to completed ')
        } else {
            SuccessMsg('Moved to todo ')
        }
        dispatch({type: types.MOVE_TODO, id: id})
    }).catch((err) => {
        ErrorMsg(err.message)
    });
};


// Set Active Component
export const activeComponent = (component) => async dispatch => {
    dispatch({type: types.ACTIVE_COMPONENT, activeComponent: component});
};


// Fetch active todos from firebase
export const fetchTodos = (status) => async (dispatch, getState) => {

    let data = [];

    const userId = getState().firebase.user.uid;


    Firestore.collection('todos').where("completed", "==", status ).where("userId", "==", userId).orderBy('date', 'desc').get().then(res => {

        res.forEach(item => {
            const doc = {
                ...item.data(),
                id: item.id
            };
            data.push(doc);

        })

        dispatch({type: types.FETCH_TODOS, todos: data, loading: false});

    })

};
