import React from 'react'
import {Menu} from 'antd';
import {NavLink} from "react-router-dom";
import * as ROUTES from '../../constants/routes'
import {DatabaseOutlined, CarryOutOutlined, PlayCircleOutlined} from '@ant-design/icons';
import NavItem from '../nav/NavItem';


const FilterMenu = () => {

    const activeStyle = {
        color: '#1890ff',
        borderBottom: '2px solid #1890ff'
}

    return (
        <Menu mode="horizontal">

        <NavItem isPrivate link={ROUTES.HOME} icon={<DatabaseOutlined />} name="All"/>

        {/* <Menu.Item 
            icon={<DatabaseOutlined />}>
                <NavLink activeStyle={activeStyle} to={ROUTES.HOME}>All</NavLink>
             </Menu.Item> */}

         <Menu.Item 
            icon={<PlayCircleOutlined />}>
            <NavLink activeStyle={activeStyle} to={ROUTES.ACTIVE}>Active</NavLink>
             </Menu.Item>

        <Menu.Item 
            icon={<CarryOutOutlined/>}>
            <NavLink activeStyle={activeStyle} to={ROUTES.COMPLETED}>Completed</NavLink>
        </Menu.Item>

    </Menu>
    )
}


export default FilterMenu
