import React from 'react'
import {Form, Input, Button} from 'antd';
import {UserOutlined, LockOutlined, InboxOutlined, PhoneOutlined} from '@ant-design/icons';
import {connect} from 'react-redux';
import styled from 'styled-components';
import { updateUser } from '../../store/actions/authActions';

const UpdateProfileFormrapper = styled.div`

    margin:3rem 0;

`;

const UpdateProfileForm = ({userMeta, updateUser}) => {

    const onFinish = (e) => {
       console.log(e)
       updateUser(e.displayName, e.password, e.phoneNumber, e.email);
    }
    return (
        <UpdateProfileFormrapper>
            <Form onFinish={onFinish}>

                <Form.Item hasFeedback name="displayName">
                    <Input prefix={<UserOutlined/>}
                         placeholder={userMeta.displayName ? userMeta.displayName : 'Your Name'}
                        allowClear/>
                </Form.Item>


                <Form.Item hasFeedback name="phoneNumber">
                    <Input prefix={<PhoneOutlined />}
                         placeholder={userMeta.phoneNumber ? userMeta.phoneNumber : "Phone Number"}
                        allowClear/>
                </Form.Item>

                <Form.Item hasFeedback name="email">
                    <Input prefix={<InboxOutlined/>}
                        placeholder="Email"
                        type="email"
                        allowClear/>
                </Form.Item>

                <Form.Item hasFeedback name="password">
                    <Input prefix={<LockOutlined/>}
                        type="password"
                        placeholder="Password"
                        allowClear/>
                </Form.Item>

                <Form.Item>
                    <Button type="primary" htmlType="submit" className="login-form-button">
                        Update
                    </Button>
                </Form.Item>
            </Form>
        </UpdateProfileFormrapper>
    )
}

const mapStateToProps = (ref) => {
    return {
        userMeta: ref.firebase.user,
    }
}


export default connect(mapStateToProps, {updateUser})(UpdateProfileForm)
