import React from 'react'
import {Tabs} from 'antd';
import {OrderedListOutlined, CarryOutOutlined} from '@ant-design/icons';
import CompletedTodos from './CompletedTodos';
import ActiveTodos from './ActiveTodos';
import {connect} from 'react-redux';
import {visibilityFilter, fetchTodos} from '../../store/actions/todosActions';


const Filter = ({visibilityFilter, fetchTodos}) => {

    const {TabPane} = Tabs;

    const onTabChange = (e) => {

        if ( e === 'active' ) {
            visibilityFilter('TODO')
            // fetchTodos(false)

        } else if ( e === 'completed' ) {
            visibilityFilter('COMPLETED')
            // fetchTodos(true)
        }
    }

    return (
        <Tabs onChange={onTabChange}
            defaultActiveKey='active'>
            <TabPane tab={
                    <span><OrderedListOutlined/>Todo</span>
                }
                key="active">
                <ActiveTodos/>
            </TabPane>

            <TabPane tab={
                    <span><CarryOutOutlined/>Completed</span>
                }
                key="completed">
                <CompletedTodos/>
            </TabPane>
        </Tabs>
    )
}


export default connect(null, {visibilityFilter, fetchTodos})(Filter)
