import React from 'react'
import { List } from 'antd';
import { connect } from 'react-redux';
import { removeTodo, moveTodo } from '../store/actions/todosActions';
import RemoveTodo from '../utils/removeTodo';
import MoveTodo from '../utils/moveTodo';

const TodoList = ({data, moveTodo, removeTodo, active, loading}) => {

    let moveToCompleted = true;

    if( active === 'TODO' ){
        moveToCompleted = true;
    } else if ( active === 'COMPLETED' ) {
        moveToCompleted = false
    }

    return (
        <div className="todo-list">
            <List
                loading={loading}
                itemLayout="horizontal"
                dataSource={data}
                renderItem={item => (
                <List.Item 
                    key={item.id}>
                    <List.Item.Meta
                    title={item.name}
                />
                <MoveTodo moveTodo={() => moveTodo(item.id, moveToCompleted)} />
                <RemoveTodo deleteTodo={() => removeTodo(item.id)} />

                </List.Item>
                )}
            />
        </div>
    )
}

const  mapStateToProps = (ref) => {
    return { 
        active: ref.state.activeComponent,
        loading: ref.state.loading
    };
  }


export default connect(mapStateToProps, { removeTodo, moveTodo })(TodoList)
