import React, { useEffect } from 'react';
import Layout from './containers/Layout';
import Todos from './containers/todos/Todos';
import Login from './containers/auth/Login';
import Register from './containers/auth/Register';
import Logout from './containers/auth/Logout'
import {BrowserRouter as Router, Switch, Route, Redirect} from "react-router-dom";
import PublicRoute  from './components/routes/PublicRoute'
import * as ROUTES from './constants/routes'
import { connect } from 'react-redux';
import { isUserLogedIn } from './store/actions/authActions';

import './App.css'


const App = ({isUserLogedIn, isAuthenticated}) => {

    useEffect(() => {
        isUserLogedIn()
    }, [isUserLogedIn])

    return (
        <Router>
            <Layout>
                <Switch>

                    {
                        isAuthenticated ? 
                        <Route exact path={ROUTES.TODOS}>
                            <Todos/>
                        </Route> : null
                    }

                   {
                       !isAuthenticated ?  
                       <PublicRoute exact path={ROUTES.LOGIN}>
                            <Login/>
                       </PublicRoute>: null
                   }

                   {
                       !isAuthenticated ? 
                       <PublicRoute exact path={ROUTES.REGISTER}>
                            <Register/>
                       </PublicRoute> : null
                   }

                    <Route exact path={ROUTES.LOGOUT}>
                        <Logout/>
                    </Route>

                    <Route path={ROUTES.UNDEFINED}>
                        <Redirect to={ROUTES.TODOS}/>
                    </Route>

                </Switch>
            </Layout>
        </Router>
    );
}

const mapStateToProps = (ref) => {
    return {
        isAuthenticated: ref.firebase.isAuthenticated
    }
}


export default connect(mapStateToProps, {isUserLogedIn})(App)
