import React, {useEffect} from 'react'
import TodoList from '../../components/todos/TodoList';
import {connect} from 'react-redux';
import {fetchTodos, visibilityFilter} from '../../store/actions/todosActions';
import AddTodoForm from '../../components/todos/AddTodoForm';
import FilterMenu from './FilterMenu';
import { ALL } from '../../constants/filter';

const AllTodos = ({fetchTodos, todos, visibilityFilter}) => {


useEffect(() => {
    const unsubscribe =  fetchTodos()
        console.log('mounted active')
        visibilityFilter(ALL)
        return () => {
            console.log('unsub active')
            //unsubscribe()
        }
    }, [fetchTodos])

    return (
        <>
        <AddTodoForm/>
        <FilterMenu />
        <TodoList data={todos}/>
        {console.log(todos)}
        </>
    )
}

const mapStateToProps = (ref) => {
    return {todos: ref.todos.items};
}

export default connect(mapStateToProps, {fetchTodos, visibilityFilter})(AllTodos)
