import React, {useEffect} from 'react'
import TodoList from '../../components/todos/TodoList';
import {connect} from 'react-redux';
import {fetchTodos} from '../../store/actions/todosActions';
import AddTodoForm from '../../components/todos/AddTodoForm';
import FilterMenu from './FilterMenu';
import { TODOS, ACTIVE, COMPLETED } from '../../constants/routes'

import { useLocation } from "react-router-dom";

const FilteredTodos = ({fetchTodos, todos}) => {

    let location = useLocation();


    useEffect(() => {
        console.log(location.pathname)
        console.log(TODOS + ACTIVE)
        if(location.pathname === TODOS + ACTIVE){
            fetchTodos(false)
        }else if (location.pathname === TODOS + COMPLETED){
            fetchTodos(true)
        }
        }, [fetchTodos])

    return (
            <>
                <AddTodoForm />
                <FilterMenu />
                {/* {console.log(todos)} */}
                {/* <TodoList data={todos}/> */}
                <p>hello</p>
            </>
    )
}

const mapStateToProps = (ref) => {
    return {todos: ref.todos.items};
}

export default connect(mapStateToProps, {fetchTodos})(FilteredTodos)
