import {types} from '../actions/actionTypes';

const inittialState = {
    todos: [],
    loading: true,
    addingNewTodo: false
}

export default(state = inittialState, action) => {

    switch (action.type) {

        case types.FETCH_TODOS:
            return Object.assign({}, state, {
                todos: action.todos,
                loading: action.loading
            });


        case types.ADD_TODO:
            return Object.assign({}, state, {
                todos: state.todos.concat(action.todo),
                addingNewTodo: action.addingNewTodo
            });


        case types.SET_CURRENT_TODO:
            return Object.assign({}, state, {currentTodoValue: action.currentTodoValue});


        case types.MOVE_TODO:
            return Object.assign({}, state, {
                todos: state.todos.filter(item => item.id !== action.id)
            });


        case types.DELETE_TODO:
            return Object.assign({}, state, {
                todos: state.todos.filter(item => item.id !== action.id)
            });


        case types.ACTIVE_COMPONENT:
            return Object.assign({}, state, {activeComponent: action.activeComponent});


        default:
            return state;
    }

};
