import {Firebase, Firestore} from '../../config/firebase'
import {types} from './actionTypes';
import {SuccessMsg, ErrorMsg} from '../../utils/helpers/msg'

export const register = (data) => async dispatch => {
    Firebase.auth().createUserWithEmailAndPassword(data.email, data.password).then((res) => {

        Firestore.collection("users").doc(res.user.uid).set({
            firstName: data.firstName,
            lastName: data.lastName
        }).then(() => {
            dispatch({type: types.REGISTER_SUCCESS})
            SuccessMsg('Successfully Registered')
        })

    }).catch(err => {
        ErrorMsg(err.message)
    })
}


export const login = (data) => async dispatch => {
    Firebase.auth().signInWithEmailAndPassword(data.email, data.password).then(() => {  
        SuccessMsg(`Welcome ${data.email}`)
    }).catch(err => {
        ErrorMsg(err.message)
    })
}


export const logout = () => async dispatch => {
    Firebase.auth().signOut().then(() => {
        SuccessMsg(`Bye`)
        dispatch({
            type: types.LOGOUT,
            payload: ''
        })
    }).catch(err => {
        ErrorMsg(err.message)
    })
}



export const initFirebase = () => async dispatch => {

    Firebase.auth().onAuthStateChanged((user) => {
        dispatch({
            type: types.FIREBASE_LOADED,
            payload: true
        })
        if (user) {
            dispatch({
                type: types.LOGIN_SUCCESS,
                payload: user
            })
        }
      });
}
