import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { TODOS } from '../../constants/routes'

const PublicRoute = ({children, path, to, isAuthenticated, isFirebaseLoaded}) => {
    return (
        ! isAuthenticated && isFirebaseLoaded ? <Route to={to} path={path}>
            {children}
        </Route>: <Redirect to={TODOS} />
    )
};

const mapStateToProps = (ref) => {
    return {
        isAuthenticated: ref.firebase.isAuthenticated,
        isFirebaseLoaded: ref.firebase.isFirebaseLoaded
    }
}

export default connect(mapStateToProps, {})(PublicRoute);
