import {connect} from 'react-redux'
import {store} from '../../store'
const isAuthenticated = ({auth}) => {
    console.log(store)
    return auth
}


const mapStateToProps = (ref) => {
    return {auth: ref.firebase.isAuthenticated};
}

export default connect(mapStateToProps, {})(isAuthenticated)
