import {connect} from 'react-redux'

const isAuthenticated = ({isAuth}) => {
    return isAuth
}

const mapStateToProps = (ref) => {
    return {isAuth: ref.firebase.isAuthenticated}
}

export default connect(mapStateToProps, {})(isAuthenticated)
