import React, { useState } from 'react'
import { Form, Input, Button } from 'antd';
import { PlusOutlined, PoweroffOutlined } from '@ant-design/icons'
import { addTodo } from '../store/actions/todosActions'
import * as firebase from 'firebase'
import { connect } from 'react-redux'
import styled from 'styled-components';

const AddForm = styled.div`
        margin-bottom:2rem;
        margin-top:2rem;
`;

const AddTodoForm = ({addTodo, userId}) => {

    const [value, setValue] = useState('');

    const inputOnChange = (e) => {
        setValue(e.target.value);
    }

    const formOnSubmit = (e) => {
        e.preventDefault();

        if ( !value ) {
            return;
        }

        const todo ={
            name: value,
            completed : false,
            date: firebase.firestore.Timestamp.fromDate(new Date()),
            userId: userId
        }

       addTodo(todo);
       setValue('');
    }

    return (
        <AddForm>
        <Form onSubmitCapture={formOnSubmit} layout="inline" >
            <Form.Item>
                <Input   onChange={inputOnChange} value={value} placeholder="Add Todo"/>
            </Form.Item>

            <Form.Item>
                <Button icon={<PoweroffOutlined />} loading type="primary" htmlType="submit">
                Add <PlusOutlined />
                </Button>
            </Form.Item>
        </Form>
    </AddForm>
    )
}

const mapStateToProps = (ref) => {
    return {
        userId: ref.firebase.user.uid
    }
}

export default connect(mapStateToProps, {addTodo})(AddTodoForm);
