import React from 'react'
import {Menu} from 'antd';
import {Link} from "react-router-dom";
import * as ROUTES from '../../constants/routes'
import {OrderedListOutlined, CarryOutOutlined} from '@ant-design/icons';
import {connect} from 'react-redux';
import {visibilityFilter, fetchTodos} from '../../store/actions/todosActions';
import { useLocation } from "react-router-dom";

const FilterMenu = ({visibilityFilter, fetchTodos}) => {

    let location = useLocation();

    let defPath = 'allTodos';

    if(location.pathname === ROUTES.TODOS){
        defPath = 'allTodos'
    }else if (location.pathname === ROUTES.TODOS+ ROUTES.ACTIVE){
        defPath = 'active'
    }else if (location.pathname === ROUTES.TODOS+ ROUTES.COMPLETED){
        defPath = 'completed'
    }else {
        defPath = 'allTodos'
    }



    console.log(location.pathname)

    return (
        <Menu selectedKeys={location.pathname}
        mode="horizontal">

        <Menu.Item key={ROUTES.TODOS}
            icon={<OrderedListOutlined />}>
                <Link to={ROUTES.TODOS}>All Todos</Link>
             </Menu.Item>

         <Menu.Item key={ROUTES.TODOS + ROUTES.ACTIVE}
            icon={<OrderedListOutlined />}>
            <Link to={ROUTES.TODOS + ROUTES.ACTIVE}>Active Todo</Link>
             </Menu.Item>

        <Menu.Item key={ROUTES.TODOS + ROUTES.COMPLETED}
            icon={<CarryOutOutlined/>}>
            <Link to={ROUTES.TODOS + ROUTES.COMPLETED}>Completed Todos</Link>
        </Menu.Item>

    </Menu>
    )
}


export default connect(null, {visibilityFilter, fetchTodos})(FilterMenu)
