import React, {useEffect} from 'react'
import TodoList from '../../components/todos/TodoList';
import {connect} from 'react-redux';
import {fetchTodos, visibilityFilter} from '../../store/actions/todosActions';
import AddTodoForm from '../../components/todos/AddTodoForm';
import FilterMenu from './FilterMenu';
import { ACTIVE } from '../../constants/filter';

const ActiveTodos = ({fetchTodos, todos, visibilityFilter}) => {

    useEffect(() => {
        fetchTodos(ACTIVE)
        visibilityFilter(ACTIVE)
        }, [fetchTodos, visibilityFilter])

    return (
            <>
                <AddTodoForm />
                <FilterMenu />
                <TodoList data={todos}/>
            </>
    )
}

const mapStateToProps = (ref) => {
    return {todos: ref.todos.items};
}

export default connect(mapStateToProps, {fetchTodos, visibilityFilter})(ActiveTodos)
