import React from 'react'
import {Menu} from 'antd';
import {BarsOutlined, UserOutlined, ExportOutlined, EditOutlined} from '@ant-design/icons';
import {Link, useLocation} from "react-router-dom";
import { connect } from 'react-redux';
import * as ROUTES from '../../constants/routes'
import styled from 'styled-components'
import NavItem from './NavItem';

const MenuWrapper = styled.div`
    ul{
        background:none;
    }
`;

const Nav = ({isAuthenticated}) => {
    return (
        <MenuWrapper>
            <Menu selectedKeys={
                    useLocation().pathname
                }
                mode="horizontal">

               <NavItem isPrivate link={ROUTES.HOME} name="Todos" />
               <NavItem isPrivate link={ROUTES.PROFILE} name="Profile" />
               <NavItem isPublic  link={ROUTES.LOGIN} name="Login" />
               <NavItem isPublic  link={ROUTES.REGISTER} name="Register" />
               <NavItem isPrivate link={ROUTES.LOGOUT} name="Logout" />

            </Menu>
        </MenuWrapper>
    )
}

const mapStateToProps = (ref) => {
    return {
        isAuthenticated: ref.firebase.isAuthenticated
    }
}


export default connect(mapStateToProps, {})(Nav)
