import React from 'react'
import {List} from 'antd';
import {connect} from 'react-redux';
import {removeTodo, moveTodo} from '../store/actions/todosActions';
import RemoveTodo from '../utils/todos/removeTodo';
import MoveTodo from '../utils/todos/moveTodo';
import styled from 'styled-components'


const TodoListWrapper = styled.div`
    margin: 2rem 0;
`;

const ListIndex = styled.h4`

    margin-right: 20px;
    width: 20px;
    display: flex;
    height: 20px;
    align-items: center;
    justify-content: center;
    position: relative;
    background: #1a90ff;
    border-radius: 3px;
    color: #fff;
    font-size: 13px;

`;

const TodoList = ({
    data,
    moveTodo,
    removeTodo,
    active,
    loading
}) => {

    let moveToCompleted = true;

    if (active === 'TODO') {
        moveToCompleted = true;
    } else if (active === 'COMPLETED') {
        moveToCompleted = false
    }

    return (
        <TodoListWrapper >
            <List  loading={loading}
                itemLayout="horizontal"
                dataSource={data}
                renderItem={
                    (item, index) => (
                        <List.Item key={
                            item.id
                        }>
                            <ListIndex>{index + 1}</ListIndex>
                            <List.Item.Meta title={
                                item.name
                            }/>
                            <MoveTodo moveTodo={
                                () => moveTodo(item.id, moveToCompleted)
                            }/>
                            <RemoveTodo deleteTodo={
                                () => removeTodo(item.id)
                            }/>

                        </List.Item>
                    )
                }/>
        </TodoListWrapper>
    )
}

const mapStateToProps = (ref) => {
    return {active: ref.todos.visibilityFilter, loading: ref.todos.loading};
}


export default connect(mapStateToProps, {removeTodo, moveTodo})(TodoList)
