import React, { useEffect } from 'react'
import styled from 'styled-components'
import { connect } from 'react-redux';
import Intro from '../../components/profile/Intro';
import Meta from '../../components/profile/Meta';
import { fetchUserProfile } from '../../store/actions/profileActions'


const ProfileWrapper = styled.div`
    margin:3rem 0;
`;

const Profile = ({firstName, fetchUserProfile, userMeta}) => {

    useEffect(() => {
        fetchUserProfile()
    }, [fetchUserProfile])

    return (
        <ProfileWrapper>
            <Intro profileName={firstName}/>
            <Meta userMeta={userMeta}/>
        </ProfileWrapper>
    )
}

const mapStateToProps = (ref) => {
    return {
        firstName: ref.userProfile.firstName,
        userMeta: ref.firebase.user
    }
}

export default connect(mapStateToProps, {fetchUserProfile})(Profile)
