import {types} from '../actions/actionTypes';

const inittialState = {
    firstName: '',
    lastName: ''
}


export default(state = inittialState, action) => {

    switch (action.type) {

        case types.FETCH_USER_PROFILE:
            return Object.assign({}, state, {
                firstName: action.firstName,
                lastName: action.lastName
            });


        default:
            return state;
    }

};
