import React, { useState } from 'react'
import { Form, Input, Button } from 'antd';
import { PlusOutlined } from '@ant-design/icons'
import { addTodo } from '../store/actions/todosActions'
import * as firebase from 'firebase'
import { connect } from 'react-redux'

const AddTodoForm = ({addTodo}) => {

    const [value, setValue] = useState('');

    const inputOnChange = (e) => {
        setValue(e.target.value);
    }

    const formOnSubmit = (e) => {
        e.preventDefault();

        if ( !value ) {
            return;
        }

        const todo ={
            name: value,
            completed : false,
            date: firebase.firestore.Timestamp.fromDate(new Date())
        }

       addTodo(todo);
       setValue('');
    }



    return (
        <div className="add-todo">
        <Form onSubmitCapture={formOnSubmit} layout="inline" >
            <Form.Item>
                <Input   onChange={inputOnChange} value={value} placeholder="Add Todo"/>
            </Form.Item>

            <Form.Item>
                <Button type="primary" htmlType="submit">
                Add <PlusOutlined />
                </Button>
            </Form.Item>
        </Form>
    </div>
    )
}

export default connect(null, {addTodo})(AddTodoForm);
