import React from 'react'
import {Menu} from 'antd';
import {Link, useRouteMatch} from "react-router-dom";
import * as ROUTES from '../../constants/routes'
import {OrderedListOutlined, CarryOutOutlined} from '@ant-design/icons';
import CompletedTodos from './CompletedTodos';
import ActiveTodos from './ActiveTodos';
import {connect} from 'react-redux';
import {visibilityFilter, fetchTodos} from '../../store/actions/todosActions';


const Filter = ({visibilityFilter, fetchTodos}) => {

    let match = useRouteMatch();

    // const {TabPane} = Tabs;

    // const onTabChange = (e) => {

    //     if ( e === 'active' ) {
    //         visibilityFilter('TODO')

    //     } else if ( e === 'completed' ) {
    //         visibilityFilter('COMPLETED')
    //     }
    // }

    return (
        <Menu selectedKeys="todo"
        mode="horizontal">

         <Menu.Item key="todo"
            icon={<OrderedListOutlined />}>
            <Link to={`${match.url}/${ROUTES.ACTIVE}`}>Todo</Link>
             </Menu.Item>

        <Menu.Item key="login"
            icon={<CarryOutOutlined/>}>
            <Link to={ROUTES.COMPLETED}>Completed</Link>
        </Menu.Item>


    </Menu>
    )
}


export default connect(null, {visibilityFilter, fetchTodos})(Filter)
