import {types} from '../actions/actionTypes';

const inittialState = {
    isAuthenticated: false
}


export default(state = inittialState, action) => {

    switch (action.type) {

        case types.REGISTER_SUCCESS:
            return Object.assign({}, state, {isAuthenticated: true});

        default:
            return state;
    }

};
