import React, { useState } from 'react'
import { Form, Input, Button } from 'antd';
import { PlusOutlined, PoweroffOutlined } from '@ant-design/icons'
import { addTodo } from '../store/actions/todosActions'
import * as firebase from 'firebase'
import { connect } from 'react-redux'
import styled from 'styled-components';

const AddForm = styled.div`
        margin-bottom:2rem;
        margin-top:2rem;
`;

const AddTodoForm = ({addTodo, userId}) => {

    const onFinish = (e) => {


        console.log(e);
        // const todo ={
        //     name: e.name,
        //     completed : false,
        //     date: firebase.firestore.Timestamp.fromDate(new Date()),
        //     userId: userId
        // }

        //addTodo(todo);


    }

    return (
        <AddForm>
        <Form onFinish={onFinish} layout="inline" >
            <Form.Item
                    name="name"
                    rules={[{ required: true, message: 'Please add a todo before ' }]}
                >
                         <Input prefix={<PlusOutlined  />} placeholder="Add Todo" />
                    </Form.Item>

            <Form.Item>
                <Button   type="primary" htmlType="submit">
                 Add 
                </Button>
            </Form.Item>
        </Form>
    </AddForm>
    )
}

const mapStateToProps = (ref) => {
    return {
        userId: ref.firebase.user.uid
    }
}

export default connect(mapStateToProps, {addTodo})(AddTodoForm);
