import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { LOGIN } from '../../constants/routes';

const PrivateRoute = ({ path, to, isAuthenticated, component, isFirebaseloaded}) => {
    return (
        isAuthenticated && isFirebaseloaded ? <Route to={to} path={path} component={component} />:
             <Redirect to={LOGIN} /> 
    )

};

const mapStateToProps = (ref) => {
    return {
        isAuthenticated: ref.firebase.isAuthenticated,
        isFirebaseloaded: ref.firebase.isFirebaseloaded
    }
}

export default connect(mapStateToProps, {})(PrivateRoute);
