import React, {useEffect} from 'react'
import TodoList from '../../components/TodoList';
import {connect} from 'react-redux';
import {fetchTodos} from '../../store/actions/todosActions';
import {isAuthenticated} from '../../utils/auth/isAuthenticated'
import {getTodos} from '../../utils/todos/getTodos';

import {store} from '../../store'

const ActiveTodos = ({fetchTodos, todos, auth}) => {

    useEffect(() => {
        fetchTodos(false)

    }, [fetchTodos])

    return (
        <div className="active-todos">
            <TodoList data={getTodos}/> {
            console.log(store.getState())
        }
            {
            console.log('from component ' + auth)
        } </div>
    )
}

const mapStateToProps = (ref) => {
    return {todos: ref.state.todos, auth: ref.firebase.isAuthenticated};
}

export default connect(mapStateToProps, {fetchTodos})(ActiveTodos)
